mapper.insert:
INSERT INTO Feed
  (content, url, date, curatorId)
VALUES (#{content}, #{url}, #{date}, #{curatorId});

mapper.update:
UPDATE Feed
SET content = #{content},
    url     = #{url},
    date    = #{date}
WHERE feedId = #{feedId};

mapper.select:
SELECT feedId, content, url, date, curatorId
FROM Feed
WHERE curatorId = #{curatorId};
