mapper.insert:
INSERT INTO Career (profileId, company, companyTitle)
VALUES

  (
  #{item.profileId}
  , #{item.company}
  , #{item.companyTitle}
  )

INSERT INTO Academic (profileId, name, major)
VALUES

  (
  #{item.profileId}
  , #{item.name}
  , #{item.major}
  )

mapper.update:
UPDATE Profile
SET title        = #{title},
    introduction = #{introduction}
WHERE curatorId = #{curatorId};

mapper.insert:
INSERT INTO Career
(careerId, profileId, company, companyTitle)
VALUES

  (
  #{item.careerId}
  , #{item.profileId}
  , #{item.company}
  , #{item.companyTitle}
  )

on duplicate key update
company=values(company)
,companyTitle=values(companyTitle)

INSERT INTO Academic
(academicId, profileId, name, major)
VALUES

  (
  #{item.academicId}
  , #{item.profileId}
  , #{item.name}
  , #{item.major}
  )

on duplicate key update
name=values(name)
,major=values(major)

mapper.select:
select careerId
from Career
where profileId = #{profileId};

select academicId
from Academic
where profileId = #{profileId};
