mapper.insert:
INSERT INTO Career (profileId, company, companyTitle)
VALUES

  (
  #{item.profileId}
  , #{item.company}
  , #{item.companyTitle}
  )

INSERT INTO Academic (profileId, name, major)
VALUES

  (
  #{item.profileId}
  , #{item.name}
  , #{item.major}
  )

mapper.update:
UPDATE Profile
SET title        = #{title},
    introduction = #{introduction}
WHERE curatorId = #{curatorId};

mapper.insert:
INSERT INTO Career
(careerId, profileId, company, companyTitle)
VALUES

  (
  #{item.careerId}
  , #{item.profileId}
  , #{item.company}
  , #{item.companyTitle}
  )

ON duplicate key update
company=values(company)
,companyTitle=values(companyTitle)

INSERT INTO Academic
(academicId, profileId, name, major)
VALUES

  (
  #{item.academicId}
  , #{item.profileId}
  , #{item.name}
  , #{item.major}
  )

ON DUPLICATE KEY UPDATE
name=values(name)
,major=values(major)

mapper.select:
SELECT careerId
FROM Career
WHERE profileId = #{profileId};

SELECT academicId
FROM Academic
WHERE profileId = #{profileId};

SELECT profileId
FROM Profile
WHERE curatorId = #{curatorId};
